<div class="register-container">
  <div class="register-box">
    <h2>Register</h2>
    <input type="text" placeholder="Username" [(ngModel)]="registerData.username">
    <input type="password" placeholder="Password" [(ngModel)]="registerData.password">
    <button (click)="onRegister()">Register</button>
  </div>
</div>
