import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-register-to-landlord',
  templateUrl: './register-to-landlord.component.html',
  styleUrls: ['./register-to-landlord.component.css']
})
export class RegisterToLandlordComponent {

  registerForm!: FormGroup;
  message = '';
  error = '';

  constructor(private fb: FormBuilder, private http: HttpClient) {
    this.registerForm = this.fb.group({
      tenantFullName: ['', Validators.required],
      username: ['', Validators.required],
      tenantEmail: ['', [Validators.required, Validators.email]],
      tenantPhoneNumber: ['', [Validators.required, Validators.pattern(/^[0-9]{10}$/)]],
      tenantPermanentAddress: ['', Validators.required],
      aadhaarNumber: ['', [Validators.required, Validators.pattern(/^\d{12}$/)]],
      panCardNumber: ['', [Validators.required, Validators.pattern(/^[A-Z]{5}[0-9]{4}[A-Z]{1}$/)]],
      landlordCode: ['', Validators.required]
    });
  }

  onSubmit(): void {
    if (this.registerForm.invalid) return;

    const token = localStorage.getItem('token');
    const username = localStorage.getItem('username');

    const headers = new HttpHeaders({
      Authorization: `Bearer ${token}`,
      username: username || ''
    });

    this.http.post<any>(
      'http://localhost:8080/api/tenant/register-to-landlord',
      this.registerForm.value,
      { headers }
    ).subscribe({
      next: (res) => {
        this.message = res.message;
        this.error = '';
        this.registerForm.reset();
      },
      error: (err) => {
        this.message = '';
        this.error = err.error?.error || 'Something went wrong';
      }
    });
  }
}
<p>register-to-landlord works!</p>
<div class="register-container">
  <h2>Register to Landlord</h2>

  <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
    <div class="form-row">
      <input type="text" placeholder="Full Name" formControlName="tenantFullName" />
    </div>
    <div class="form-row">
      <input type="text" placeholder="Username" formControlName="username" />
    </div>
    <div class="form-row">
      <input type="email" placeholder="Email" formControlName="tenantEmail" />
    </div>
    <div class="form-row">
      <input type="text" placeholder="Phone Number" formControlName="tenantPhoneNumber" />
    </div>
    <div class="form-row">
      <textarea placeholder="Permanent Address" formControlName="tenantPermanentAddress"></textarea>
    </div>
    <div class="form-row">
      <input type="text" placeholder="Aadhaar Number" formControlName="aadhaarNumber" />
    </div>
    <div class="form-row">
      <input type="text" placeholder="PAN Card Number" formControlName="panCardNumber" />
    </div>
    <div class="form-row">
      <input type="text" placeholder="Landlord Code" formControlName="landlordCode" />
    </div>

    <button type="submit" [disabled]="registerForm.invalid">Register</button>
  </form>

  <div class="message success" *ngIf="message">{{ message }}</div>
  <div class="message error" *ngIf="error">{{ error }}</div>
</div>
