import { Component, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-register-to-landlord',
  templateUrl: './register-to-landlord.component.html',
  styleUrls: ['./register-to-landlord.component.css']
})
export class RegisterToLandlordComponent implements OnInit {

  registerForm!: FormGroup;
  message = '';
  error = '';
  isSubmitting = false;

  private token: string | null = null;
  private username: string | null = null;

  constructor(private fb: FormBuilder, private http: HttpClient) {}

  ngOnInit(): void {
    // Setup the form and load auth data
    this.registerForm = this.fb.group({
      tenantFullName: ['', Validators.required],
      tenantEmail: ['', [Validators.required, Validators.email]],
      tenantPhoneNumber: ['', [Validators.required, Validators.pattern(/^[0-9]{10}$/)]],
      tenantPermanentAddress: ['', Validators.required],
      aadhaarNumber: ['', [Validators.required, Validators.pattern(/^\d{12}$/)]],
      panCardNumber: ['', [Validators.required, Validators.pattern(/^[A-Z]{5}[0-9]{4}[A-Z]{1}$/)]],
      landlordCode: ['', Validators.required]
    });

    this.token = localStorage.getItem('token');
    this.username = localStorage.getItem('username');
  }

  onSubmit(): void {
    console.log('Form Submitted')
    if (this.registerForm.invalid || this.isSubmitting || !this.token || !this.username) {
      return;
    }

    this.isSubmitting = true;

    const headers = new HttpHeaders({
      Authorization: `Bearer ${this.token}`,
      username: this.username
    });

    this.http.post<any>(
      'http://localhost:8080/api/tenant/register-to-landlord',
      this.registerForm.value,
      { headers }
    ).subscribe({
      next: (res) => {
        this.message = res.message;
        this.error = '';
        this.registerForm.reset();
        this.isSubmitting = false;
      },
      error: (err) => {
        this.message = '';
        this.error = err.error?.error || 'Something went wrong';
        this.isSubmitting = false;
      }
    });
  }
}
<div class="register-bg">
  <div class="register-container">
    <h2>Tenant Registration under Landlord</h2>

    <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
      <!-- Tenant Full Name -->
      <div class="form-row">
        <label>Full Name:</label>
        <input formControlName="tenantFullName" placeholder="Enter full name" />
        <div *ngIf="registerForm.get('tenantFullName')?.touched && registerForm.get('tenantFullName')?.invalid" class="error">
          Full name is required.
        </div>
      </div>

      <!-- Email -->
      <div class="form-row">
        <label>Email:</label>
        <input formControlName="tenantEmail" type="email" placeholder="Enter email" />
        <div *ngIf="registerForm.get('tenantEmail')?.touched && registerForm.get('tenantEmail')?.invalid" class="error">
          Enter a valid email.
        </div>
      </div>

      <!-- Phone Number -->
      <div class="form-row">
        <label>Phone Number:</label>
        <input formControlName="tenantPhoneNumber" placeholder="Enter phone number" />
        <div *ngIf="registerForm.get('tenantPhoneNumber')?.touched && registerForm.get('tenantPhoneNumber')?.invalid" class="error">
          Enter a valid 10-digit phone number.
        </div>
      </div>

      <!-- Permanent Address -->
      <div class="form-row">
        <label>Permanent Address:</label>
        <textarea formControlName="tenantPermanentAddress" placeholder="Enter address"></textarea>
        <div *ngIf="registerForm.get('tenantPermanentAddress')?.touched && registerForm.get('tenantPermanentAddress')?.invalid" class="error">
          Address is required.
        </div>
      </div>

      <!-- Aadhaar Number -->
      <div class="form-row">
        <label>Aadhaar Number:</label>
        <input formControlName="aadhaarNumber" placeholder="Enter 12-digit Aadhaar" />
        <div *ngIf="registerForm.get('aadhaarNumber')?.touched && registerForm.get('aadhaarNumber')?.invalid" class="error">
          Enter a valid 12-digit Aadhaar number.
        </div>
      </div>

      <!-- PAN Card Number -->
      <div class="form-row">
        <label>PAN Card Number:</label>
        <input formControlName="panCardNumber" placeholder="Enter PAN number" />
        <div *ngIf="registerForm.get('panCardNumber')?.touched && registerForm.get('panCardNumber')?.invalid" class="error">
          Enter a valid PAN (e.g., ABCDE1234F).
        </div>
      </div>

      <!-- Landlord Code -->
      <div class="form-row">
        <label>Landlord Code:</label>
        <input formControlName="landlordCode" placeholder="Enter landlord code" />
        <div *ngIf="registerForm.get('landlordCode')?.touched && registerForm.get('landlordCode')?.invalid" class="error">
          Landlord code is required.
        </div>
      </div>

      <!-- Submit Button -->
      <button type="submit" [disabled]="registerForm.invalid || isSubmitting">
        {{ isSubmitting ? 'Registering...' : 'Register' }}
      </button>
    </form>

    <!-- Success Message -->
    <div *ngIf="message" class="message success">
      {{ message }}
    </div>

    <!-- Error Message -->
    <div *ngIf="error" class="message error">
      {{ error }}
    </div>
  </div>
</div>
