<div class="login-container">
  <div class="login-box">
    <h2>Login</h2>

    <input type="text" placeholder="Username" [(ngModel)]="loginData.username">

    <input [type]="showPassword ? 'text' : 'password'" placeholder="Password" [(ngModel)]="loginData.password">

    <div class="show-password">
      <input type="checkbox" id="showPassword" [(ngModel)]="showPassword">
      <label for="showPassword">Show Password</label>
    </div>

    <button (click)="onLogin()">Login</button>
  </div>
</div>
