import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-register-to-landlord',
  templateUrl: './register-to-landlord.component.html',
  styleUrls: ['./register-to-landlord.component.css']
})
export class RegisterToLandlordComponent {

  registerForm!: FormGroup;
  message = '';
  error = '';

  constructor(private fb: FormBuilder, private http: HttpClient) {
    this.registerForm = this.fb.group({
      tenantFullName: ['', Validators.required],
      username: ['', Validators.required],
      tenantEmail: ['', [Validators.required, Validators.email]],
      tenantPhoneNumber: ['', [Validators.required, Validators.pattern(/^[0-9]{10}$/)]],
      tenantPermanentAddress: ['', Validators.required],
      aadhaarNumber: ['', [Validators.required, Validators.pattern(/^\d{12}$/)]],
      panCardNumber: ['', [Validators.required, Validators.pattern(/^[A-Z]{5}[0-9]{4}[A-Z]{1}$/)]],
      landlordCode: ['', Validators.required]
    });
  }

  onSubmit(): void {
    if (this.registerForm.invalid) return;

    const token = localStorage.getItem('token');
    const username = localStorage.getItem('username');

    const headers = new HttpHeaders({
      Authorization: `Bearer ${token}`,
      username: username || ''
    });

    this.http.post<any>(
      'http://localhost:8080/api/tenant/register-to-landlord',
      this.registerForm.value,
      { headers }
    ).subscribe({
      next: (res) => {
        this.message = res.message;
        this.error = '';
        this.registerForm.reset();
      },
      error: (err) => {
        this.message = '';
        this.error = err.error?.error || 'Something went wrong';
      }
    });
  }
}
<div class="register-bg">
  <div class="register-container">
    <h2>Register to Landlord</h2>

    <form (ngSubmit)="registerTenant()" #tenantForm="ngForm">
      <div class="form-row">
        <input type="text" name="fullName" [(ngModel)]="tenant.tenantFullName" placeholder="Full Name" required />
      </div>

      <div class="form-row">
        <input type="text" name="username" [(ngModel)]="tenant.username" placeholder="Username" required />
      </div>

      <div class="form-row">
        <input type="email" name="email" [(ngModel)]="tenant.tenantEmail" placeholder="Email" required />
      </div>

      <div class="form-row">
        <input type="text" name="phoneNumber" [(ngModel)]="tenant.tenantPhoneNumber" placeholder="Phone Number" required />
      </div>

      <div class="form-row">
        <textarea name="permanentAddress" [(ngModel)]="tenant.tenantPermanentAddress" placeholder="Permanent Address" required></textarea>
      </div>

      <div class="form-row">
        <input type="text" name="aadhaarNumber" [(ngModel)]="tenant.aadhaarNumber" placeholder="Aadhaar Number" required />
      </div>

      <div class="form-row">
        <input type="text" name="panCardNumber" [(ngModel)]="tenant.panCardNumber" placeholder="PAN Card Number" required />
      </div>

      <div class="form-row">
        <input type="text" name="landlordCode" [(ngModel)]="tenant.landlordCode" placeholder="Landlord Code" required />
      </div>

      <button type="submit" [disabled]="!tenantForm.form.valid">Register</button>
    </form>

    <div class="message" *ngIf="message" [ngClass]="{'success': isSuccess, 'error': !isSuccess}">
      {{ message }}
    </div>
  </div>
</div>
