import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-update-tenant-profile',
  templateUrl: './update-tenant-profile.component.html',
  styleUrls: ['./update-tenant-profile.component.css']
})
export class UpdateTenantProfileComponent implements OnInit{

  updateForm!: FormGroup;
  message: string = '';
  error: string = '';

  constructor(
    private fb: FormBuilder,
    private http: HttpClient,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.updateForm = this.fb.group({
      tenantFullName: ['', [Validators.required]],
      tenantEmail: ['', [Validators.required, Validators.email]],
      tenantPhoneNumber: ['', [Validators.required, Validators.pattern(/^[6-9]\d{9}$/)]],
      tenantPermanentAddress: ['', [Validators.required]],
      aadhaarNumber: ['', [Validators.required, Validators.pattern(/^\d{12}$/)]],
      panCardNumber: ['', [Validators.required, Validators.pattern(/^[A-Z]{5}[0-9]{4}[A-Z]{1}$/)]]
    });
  }

  onSubmit(): void {
    if (this.updateForm.invalid) {
      this.error = 'Please fill in all required fields correctly.';
      return;
    }

    const headers = new HttpHeaders({
      'Authorization': 'Bearer ' + localStorage.getItem('token')
    });

    this.http.put<any>('/api/tenant/update/profile', this.updateForm.value, { headers })
      .subscribe({
        next: (response) => {
          this.message = response.message;
          this.error = '';
        },
        error: (err) => {
          this.error = err.error?.message || 'Update failed. Please try again.';
          this.message = '';
        }
      });
  }
}
<div class="update-container">
  <h2 class="heading">Update Your Profile</h2>

  <form [formGroup]="updateForm" (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label>Full Name:</label>
      <input type="text" formControlName="tenantFullName" placeholder="Enter full name" />
    </div>

    <div class="form-group">
      <label>Email:</label>
      <input type="email" formControlName="tenantEmail" placeholder="Enter email" />
    </div>

    <div class="form-group">
      <label>Phone Number:</label>
      <input type="text" formControlName="tenantPhoneNumber" placeholder="Enter phone number" />
    </div>

    <div class="form-group">
      <label>Permanent Address:</label>
      <textarea formControlName="tenantPermanentAddress" placeholder="Enter address"></textarea>
    </div>

    <div class="form-group">
      <label>Aadhaar Number:</label>
      <input type="text" formControlName="aadhaarNumber" placeholder="Enter 12-digit Aadhaar" />
    </div>

    <div class="form-group">
      <label>PAN Card Number:</label>
      <input type="text" formControlName="panCardNumber" placeholder="Enter PAN number" />
    </div>

    <button type="submit" class="submit-button">Update Profile</button>

    <p class="success-msg" *ngIf="message">{{ message }}</p>
    <p class="error-msg" *ngIf="error">{{ error }}</p>
  </form>
</div>
